list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX vcard: <http://www.w3.org/2006/vcard/ns#> PREFIX fn: <http://www.w3.org/2005/xpath-functions#>
SELECT DISTINCT ?authorship ?rank ?author (MAX(?authorName_) AS ?authorName) (MAX(?corresponding_) AS ?corresponding) WHERE { ?subject ?property ?authorship . ?authorship a core:Authorship . ?authorship a core:Authorship . ?authorship core:rank ?rank . ?authorship a core:Authorship . ?authorship core:relates ?author . ?author a foaf:Agent . ?author rdfs:label ?authorName_ . OPTIONAL { ?authorship core:isCorrespondingAuthor ?corresponding_ FILTER(?corresponding == true) }
FILTER ( bound(?author) )
} GROUP BY ?authorship ?rank ?author ORDER BY ?rank (fn:lower-case(?authorName))
